import { useEffect } from "react";
import { useRequestData } from "../../../../hooks/useRequestData";
import { MainContainer } from "./styled";
import { Lottery, LotteryProps } from "./types";



export const LotteryInfo = ({setLottery}:LotteryProps) => { 
  const [lotteries, getLotteries] = useRequestData({
    initialData: [],
    url: "/loterias",
  });

  useEffect(() => {
    getLotteries();
  }, []);

  const handleLoterries = (e: { target: { value: string; }; }): void => {
    setLottery(e.target.value)
  }

  const showSelectOptions = ():HTMLSelectElement[] => {
    return lotteries?.map((item:Lottery) =>{ 
      return(
        <option value={item.id}> {item.nome} </option>
      )

    })
  }

  return (
    <MainContainer>
      <select 
      placeholder="Loteria" 
      onChange={handleLoterries}
      required>
        <option value={""}> Escolha uma opção </option>
        {showSelectOptions()}
      </select>

      <h3>Mega sena</h3>

      <p>some info</p>
    </MainContainer>
  );
};
