import styled from "styled-components";
import { diadesorteColor, lotofacilColor, lotomaniaColor, megasenaColor, quinaColor, timemaniaColor } from "../../../../constants/themeColors";

export const MainContainer = styled.div<{ lotteryId: string }>`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  height: 100%;
  align-content: flex-start;
  width: 35%;
  border-radius: 0 -30% -30% 0;
  background: ${(props) => {
    switch (props.lotteryId) {
      case "0":
        return megasenaColor;
      case "1":
        return quinaColor;
      case "2":
        return lotofacilColor;
      case "3":
        return lotomaniaColor;
      case "4":
        return timemaniaColor;
      case "5":
        return diadesorteColor;
      default:
        return "white";
    }
  }};
`;
