import { useEffect } from "react";
import { useRequestData } from "../../../../hooks/useRequestData";
import { MainContainer } from "./styled";

export const LotteryInfo = (props:any) => {
  const [Lotteries, getLotteries] = useRequestData({
    initialData: [],
    url: "/loterias",
  });

  useEffect(() => {
    getLotteries();
  }, []);

  const handleLoterries = (e: { target: { value: any; }; }) => {
    props.setLottery(e.target.value)
  }

  return (
    <MainContainer>
      <select 
      placeholder="Loteria" 
      onChange={handleLoterries}
      required>
        <option value={0}> Mega-sena </option>
      </select>

      <h3>Mega sena</h3>

      <p>some info</p>
    </MainContainer>
  );
};
